mapper.update:
update axleInspection set
ultAxleLeft = #{ ultAxleLeft }, ultAxleRight = #{ ultAxleRight }, reultAxleLeft = #{ reultAxleLeft }, reultAxleRight = #{ reultAxleRight }, ultAxleFootLeft = #{ ultAxleFootLeft }, ultAxleFootRight = #{ ultAxleFootRight }, ultAxleNeckLeft = #{ ultAxleNeckLeft }, ultAxleNeckRight = #{ ultAxleNeckRight }, reultAxleNeckLeft = #{ reultAxleNeckLeft }, reultAxleNeckRight = #{ reultAxleNeckRight },
ultisFinish = #{ ultisFinish }, ultfinishTime = #{ ultfinishTime }, worker = #{ worker }, reultisFinish = #{ reultisFinish }, reultfinishTime = #{ reultfinishTime }, reultInspector = #{ reultInspector } where wheelId=#{wheelId}

update axleInspection set magAxleBody = #{ magAxleBody }, magtAxleNeckLeft = #{ magtAxleNeckLeft }, magAxleNeckRight = #{ magAxleNeckRight },
magInspector = #{ magInspector }, magisFinish = #{ magisFinish }, magfinishTime = #{ magfinishTime }
where wheelId=#{wheelId}

mapper.select:
SELECT w.* FROM wheelinfo w

    JOIN axleinspection r ON w.`WheelId` = r.`wheelId`

WHERE (w.isaxleInspectionFinish != '-1' and ismagnetInspectionFinish = '1' and isbearingRepairFinish = '1')

    AND  w.wheelId = #{wheelId}


    AND  w.takeInDate = #{takeInDate}


    AND  w.axleNumber = #{axleNumber}


    AND w.vehicleNumber = #{vehicleNumber}


    AND r.ultfinishTime like #{infoTakeFinishTime}

SELECT w.* FROM wheelinfo w

    JOIN axleinspection r ON w.`WheelId` = r.`wheelId`

WHERE (w.ismagnetInspectionFinish != '-1' and isbearingRepairFinish = '1')

    AND  w.wheelId = #{wheelId}


    AND  w.takeInDate = #{takeInDate}


    AND  w.axleNumber = #{axleNumber}


    AND w.vehicleNumber = #{vehicleNumber}


    AND r.magfinishTime like #{infoTakeFinishTime}
